import React,{useState} from 'react'

const Search = () => {
    const [text,setText]= useState('')

    const onChange = (q) => {
        setText(q)
        getQuery(q)
    }
    return (
        <section classname='search'>
            <form>
                <input type='text' 
                className='form-control' 
                placeholder='Search characters'
                value={text}
                onChange={(e)=> onChange(e.target.value)}
                autoFocus
                />
            </form>
            </section>
    )
}

export default Search
