class App extends Component {
  constructor (props){
    super(props);
    this.state={
      loading: true,
      pokemon:[],
      url:'https://pokeapi.co/api/v2/pokemon/'
    }
  }

  componentDidMount(){
    this.getPokemon();
  }

  getPokemon = () =>{
    this.setState({loading:true})
    fetch(this.state.url)
    .then(res=>res.json())
    .then(res=>{
      this.setState({
        pokemon : res.results,
        url : res.next,
        loaging:false
      })
    })

  };
